    }
  return (
    <div>
        <h3>INBOX</h3>
        {!newTask && (
            <button className='new' onClick={newtaskHandler}>+Add New</button>
        )}
        {newTask && (
            <form className='newtask-box'>
                <input type='text' ref={titleRef}/>
            </form>
        )}
    </div>
  )
}
