import React, { useState } from 'react'

const list = [
    {number:1,title:"lets Complete this",date:new Date("25/01/2023")},
    {number:2,title:"Should sleep at 9 pm",date:new Date("28/01/2023")},
    {number:3,title:"Should complete react ",date:new Date("02/02/2023")},

]

export default function MainSection(props) {
    const [filteredList,setFilteredlist] = useState(list)
    const addToList = (obj)=>{
        list.push(obj)
        setFilteredlist(list)
    }
  return (
    <div className ='Main-section'>
        {props.active === "INBOX" && (
            <Inbox list={filteredList} append={addToList}/>
        )}
        {props}

    </div>
  )
}
