import React, { useRef, useState } from 'react'

export default function Inbox(props) {
    const [newTask,setNewTask] = useState(false)
    const titleRef = useRef()
    const calendarRef = useRef()
    const newtaskHandler=()=>{
        setNewTask(true)
    }
    const addHandler=(e)=>{
        e.preventDefault()
        if(titleRef.current.value === ""){
            window.alert("Task Can Not Be Empty")
            return;

        }
        const newObj = {
            number : props.list.length+1,
            title : titleRef.current.value,
            date : new Date(calendarRef.current.value)
        }
        
        props.append(newObj)
        setNewTask(false)
    }
    const cancelHandler=()=>{
        setNewTask(false)
    }
    const deleteHandler = (index) => {
        const updatedList = props.list.filter((task,i) => i !== index)
        props.setList(updatedList)
        }
   
    
  return (
    <div>
        <h3 className='Heading'>INBOX</h3>
        {!newTask && (
            <button className='new' onClick={newtaskHandler}>+Add New</button>
        )}
        {newTask && (
            <form className='newtask-box'>
                <input type='text' ref={titleRef} placeholder='Write your task'/>
                <div className='button'>
                    <button className='new' onClick={addHandler}>Add Task</button>
                    <button className='new' onClick={cancelHandler}>Cancel</button>
                    <input type="date" ref={calendarRef} defaultValue="2023-01-04"/>
                </div>
            </form>
        )}
        <div>
            {props.list.map((list,index)=>{
                return(
                    <div className='box' key={index}>
                        {list.title} ({list.date.toLocaleString()})
                        <button className="delete" onClick={() => deleteHandler(index)}>Delete</button>

                    </div>
                )
            })}
        </div>
    </div>
  )
}
